mapper.update:
update sys_admin
set token = #{token}
where id = #{id}

update sys_admin
set online_status = #{status}
where id = #{id}

update sys_admin
set account_status = #{status}
where id = #{id}

mapper.select:
select t1.*,(t2.department_id) orgId from sys_Admin t1 left join sys_admin_department t2 ON t1.id = t2.admin_id
where t1.username = #{username}
  and t1.deleted = 0
