mapper.update:
update sys_admin_info t1
set last_login_time = #{lastLoginTime}
where admin_id = #{id}
  and t1.deleted = 0

mapper.select:
select *
from sys_admin t1,
     sys_admin_info t2
where t1.id = t2.admin_id
  and t1.id = #{id}

select t1.id,t1.username,t2.real_name,t2.admin_id,t2.avatar,t2.gender,t1.phone,t2.email,t1.create_time,t1.account_status,t3.name as orgName from sys_admin t1,sys_admin_info t2,sys_department t3,sys_admin_department t4 where t1.id = t2.admin_id and t1.id = t4.admin_id and t3.id = t4.department_id and t1.deleted = 0 and t2.deleted = 0 and t3.deleted = 0 and t4.deleted = 0
and t1.username = #{username}
and t1.phone = #{phone}
and t2.gender = #{gender}

select *
from sys_admin t1,
     sys_admin_info t2
where t1.id = t2.admin_id
  and t1.id = #{id}
