mapper.select:
SELECT COUNT(*) FROM t_owner b WHERE b.deleted=0

        AND b.state=#{state}

        AND b.identity=0 AND b.house_id IN(
        SELECT id FROM t_house


        community_name LIKE CONCAT('%',#{communityName},'%')


)

SELECT b.id,c.avatar,c.username, CONCAT(a.community_name,a.building_name,a.units,'单元',a.house_number) as houseName, c.real_name,c.phone,b.identity_card,b.identity,b.update_time,b.state FROM t_house a,t_owner b,t_user c WHERE b.deleted=0
AND b.state=#{state}
AND b.identity=0 AND b.house_id IN( SELECT id FROM t_house
community_name LIKE CONCAT('%',#{communityName},'%')
) AND b.user_id = c.id and a.id=b.house_id LIMIT #{start},#{limit}
